import React, { useEffect } from 'react'
import SearchBox from './SearchBox'
import ContactItem from './ContactItem'
import { UseSelector, useSelector } from 'react-redux/es/hooks/useSelector'

const ContactList = () => {
  const contactList = useSelector(state=>state.contactList)
  let [searchList, setSearchList] = useState([]);
  useEffect(()=>{
    if(keyword !=="") {
      let list = contactList.search((item)=>item.name.includes(keyword));
      setSearchList(list);
    } else {
      setSearchList(contactList);
    }
  }, [keyword, contactList])
  return (
    <div>
      <div className='contactList-title'>연락처 리스트</div>
      num:{searchList.length}
      <SearchBox/>
      {searchList.map((item,idx)=>(<ContactItem key={idx} item={item}/>))}
    </div>
  )
}

export default ContactList
